<span class="line"><span style="color:#A6ACCD;">dbpath </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">usr</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">local</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">mongodb</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">mongodb3</span><span style="color:#89DDFF;">.</span><span style="color:#F78C6C;">6.3</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">data</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">koa2</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">blog</span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;">db</span></span>
